import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './css/styles.css';

const USER_LEVELS = {
  KINDERGARTEN: 'kindergarten',
  PRIMARY: 'primary',
  JUNIOR_SECONDARY: 'junior secondary',
  HIGH_SCHOOL: 'high school',
  COLLEGE: 'college',
  UNIVERSITY: 'university',
  SPECIALIZED: 'specialized education',
};

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    userLevel: '',
    userType: 'user', // Default to user
    childName: '', // Child name for parents
    childGrade: '', // Child grade for parents
  });

  const { username, email, password, userLevel, userType, childName, childGrade } = formData;
  const navigate = useNavigate(); // Initialize useNavigate

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Prepare the data based on user type
      const dataToSubmit = userType === 'parent' 
        ? { username, email, password, childName, childGrade }
        : { username, email, password, userLevel };
        
      await axios.post('http://localhost:5000/users/register', dataToSubmit);
      alert('User registered successfully!');
      navigate('/'); // Navigate to login page after successful registration
    } catch (error) {
      alert('Error during registration: ' + (error.response?.data?.error || 'An unexpected error occurred'));
      console.error(error.response?.data || error);
    }
  };

  return (
    <div className="register-container">
      <h2>Create an Account</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          name="username" 
          value={username} 
          onChange={handleChange} 
          placeholder="Username" 
          required 
        />
        <input 
          type="email" 
          name="email" 
          value={email} 
          onChange={handleChange} 
          placeholder="Email" 
          required 
        />
        <input 
          type="password" 
          name="password" 
          value={password} 
          onChange={handleChange} 
          placeholder="Password" 
          required 
        />
        
        <div>
          <label>
            <input 
              type="radio" 
              name="userType" 
              value="user" 
              checked={userType === 'user'} 
              onChange={handleChange} 
            />
            User
          </label>
          <label>
            <input 
              type="radio" 
              name="userType" 
              value="parent" 
              checked={userType === 'parent'} 
              onChange={handleChange} 
            />
            Parent
          </label>
        </div>
        
        {userType === 'user' && (
          <select 
            name="userLevel" 
            value={userLevel} 
            onChange={handleChange} 
            required
          >
            <option value="">Select User Level</option>
            {Object.entries(USER_LEVELS).map(([key, value]) => (
              <option key={key} value={value}>{value.replace(/_/g, ' ')}</option>
            ))}
          </select>
        )}

        {userType === 'parent' && (
          <>
            <input 
              type="text" 
              name="childName" 
              value={childName} 
              onChange={handleChange} 
              placeholder="Child's Name" 
              required 
            />
            <input 
              type="text" 
              name="childGrade" 
              value={childGrade} 
              onChange={handleChange} 
              placeholder="Child's Grade" 
              required 
            />
          </>
        )}
        
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
