// src/ChildProfile.js
import React from 'react';
import './css/ChildProfile.css'; // Optional: Create a CSS file for styles

const ChildProfile = ({ child }) => {
    return (
        <div className="child-profile">
            <h3>{child.name}'s Profile</h3>
            <p><strong>Grade:</strong> {child.grade}</p>
            <p><strong>Progress:</strong> {child.progress}%</p>
            <p><strong>Learning Goals:</strong></p>
            <ul>
                {child.learningGoals && child.learningGoals.length > 0 ? (
                    child.learningGoals.map((goal, index) => (
                        <li key={index}>{goal.description} - Progress: {goal.progress}%</li>
                    ))
                ) : (
                    <li>No learning goals set.</li>
                )}
            </ul>
            <p><strong>Recent Activities:</strong></p>
            <ul>
                {child.recentActivities && child.recentActivities.length > 0 ? (
                    child.recentActivities.map((activity, index) => (
                        <li key={index}>{activity.description}</li>
                    ))
                ) : (
                    <li>No recent activities.</li>
                )}
            </ul>
            <p><strong>Notifications:</strong></p>
            <ul>
                {child.notifications && child.notifications.length > 0 ? (
                    child.notifications.map((notification, index) => (
                        <li key={index}>{notification.message}</li>
                    ))
                ) : (
                    <li>No notifications.</li>
                )}
            </ul>
        </div>
    );
};

export default ChildProfile;
